CREATE DEFINER=`root`@`localhost` PROCEDURE `crud_autor` (IN `var_codigo_autor` INT(11), `var_nome_autor` VARCHAR(80), `var_email` VARCHAR(80), `var_senha` VARCHAR(80), `var_verificar_senha` VARCHAR(80), `opcao` INT)   BEGIN
   	IF(EXISTS(SELECT codigo_autor FROM autor WHERE codigo_autor = var_codigo_autor)) THEN
    	IF(opcao = 1) THEN
        	UPDATE autor SET nome_autor = var_nome_autor, email = var_email, senha = var_senha, verificar_senha = var_verificar_senha WHERE codigo_autor = var_codigo_autor;
        ELSE
        	DELETE FROM autor WHERE codigo_autor = var_codigo_autor;
        END IF;    
    ELSE
    	INSERT INTO autor VALUES (var_codigo_autor, var_nome_autor, var_email,  var_senha, var_verificar_senha);
    END IF;
   END$$

CREATE DEFINER=`root`@`localhost` PROCEDURE `listar_autor` (IN `var_codigo_autor` INT)   BEGIN    	
        IF (var_codigo_autor IS NULL) THEN
        	SELECT * FROM autor ORDER BY nome_autor;
        ELSE
        	SELECT * FROM autor WHERE var_codigo_autor = var_codigo_autor;
        END IF;        
    END$$

CREATE DEFINER=`root`@`localhost` PROCEDURE `listar_categoria` (IN `var_id` INT)   BEGIN
	
    IF (var_id IS NULL) THEN
    	SELECT * FROM categoria;
     ELSE
		SELECT * FROM categoria WHERE id = var_id;
     END IF;

	END$$

